import React, { Component } from 'react';
import { connect } from 'react-redux';
import { searchMovie } from '../../actions/searchActions'

class SearchForm extends Component {
    
    handleChange = (e) => {
        const name = this.props.searchMovie.name;
        name = (e.target.value);
        console.log(name)
    };
    handleSubmit = (e) => {
        e.preventDefault();
    }
    render(){
        return (
            <form className="searchBar" onSubmit={this.handleSubmit}>
                <div className="input-field col s6">
                    <label htmlFor="search">search a movie or TV series</label>
                    <input type="text" className="white-text center" onChange={this.handleChange}/>
                </div>
            </form>
        )
    }    
}

