import React, { Component } from 'react'
import { connect } from 'react-redux'
import {fetchMovie} from '../../actions/searchActions'

class Movie extends Component {
    componentDidMount(){
        this.props.fetchMovie(this.props.match.params.id)
    }
    render() {
        return (
            <div className="container">
                <ul className="collection with-header white-text ">
        <li className="collection-header black"><h4>{this.props.movie.Title} - {this.props.movie.Year}</h4></li>
        <li className="collection-item black"><b>IMDB rating : </b><Text className="grey-text text-lighten-1">{this.props.movie.Rated}</Text></li>
                    <li className="collection-item black"><b>Released Date : </b><Text className="grey-text text-lighten-1">{this.props.movie.Released}</Text></li>
                    <li className="collection-item black"><b>Runtime : </b><Text className="grey-text text-lighten-1">{this.props.movie.Runtime}</Text></li>
                    <li className="collection-item black"><b>Genre : </b><Text className="grey-text text-lighten-1">{this.props.movie.Genre}</Text></li>
                    <li className="collection-item black"><b>Director : </b><Text className="grey-text text-lighten-1">{this.props.movie.Director}</Text></li>
                    <li className="collection-item black"><b>Writer : </b><Text className="grey-text text-lighten-1">{this.props.movie.Writer}</Text></li>
                    <li className="collection-item black"><b>Actors : </b><Text className="grey-text text-lighten-1">{this.props.movie.Actors}</Text></li>
                    <li className="collection-item black"><b>Plot : </b><Text className="grey-text text-lighten-1">{this.props.movie.Plot}</Text></li>
                    <li className="collection-item black"><b>Language : </b><Text className="grey-text text-lighten-1">{this.props.movie.Language}</Text></li>
                    <li className="collection-item black"><b>Country : </b><Text className="grey-text text-lighten-1">{this.props.movie.Country}</Text></li>
                    <li className="collection-item black"><b>Awards : </b><Text className="grey-text text-lighten-1">{this.props.movie.Awards}</Text></li>
                </ul>
            </div>
         
        )
    }
}

const mapStateToProps = state => ({
    loading: state.movies.loading,
    movie: state.movies.movie
})

export default connect(mapStateToProps, {fetchMovie})(Movie);
