import React, { Component } from 'react'
import { Link } from 'react-router-dom'

class MovieCard extends Component {
    render() {
        const {movie} = this.props;
        return (
        <div className="col s12 m6 l4 xl3">
            <div className="card" id="card" style={{minHeight: '300px', minWidth: 'fit-content', maxHeight: '400px', maxWidth: 'fit-content', marginBottom: '45%', position: 'inherit', backgroundColor: 'black'}}>
                <div className="card-image">
                    <img src={movie.Poster} alt="movie cover"/>
                    {/* <p className="card-title flow-text" style={{textShadow: '0px 0px 10px #000000'}}>{movie.Title} - {movie.Year}</p> */}
                </div>
                <div className="card-action red accent-4" style={{height: '75px', textAlign: 'start'}}>
                    <Link to={'/movie/' + movie.imdbID}>more details</Link>
                </div>
            </div>
        </div>
        )
    }
}

export default MovieCard;
