import React, { Component } from 'react';
import { connect } from 'react-redux';
import { searchMovie, fetchmovies } from '../../actions/searchActions';

class SearchForm extends Component {
    
    handleChange = (e) => {
        this.props.searchMovie(e.target.value);
    };
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.fetchmovies(this.props.text);
    }
    render(){
        return (
            <form className="container" onSubmit={this.handleSubmit}>
                <div className="input-field col s6">
                    <label htmlFor="search">search a movie or TV series</label>
                    <input type="text" className="white-text center" onChange={this.handleChange}/>
                    <a className="waves-effect waves-light btn-large" onSubmit={this.handleSubmit}><i className="material-icons left">search</i>SEARCH</a>
                </div>
            </form>
        )
    }    
}

const mapStateToProps = (state) => ({
        text: state.movies.text
})

export default connect(mapStateToProps, {searchMovie, fetchmovies})(SearchForm);
