import React, { Component } from 'react'
import { connect } from 'react-redux'
import {fetchMovie} from '../../actions/searchActions'

class Movie extends Component {
    componentDidMount(){
        this.props.fetchMovie(this.props.match.params.id)
    }
    render() {
        return (
            <div className="container">
                <ul className="collection with-header white-text ">
        <li className="collection-header grey darken-4"><h4>{this.props.movie.Title} - {this.props.movie.Year}</h4></li>
        <li className="collection-item grey darken-4"><b>IMDB rating : </b>{this.props.movie.Ratings.imdbRating}</li>
                    <li className="collection-item grey darken-4"><b>Released Date : </b>{this.props.movie.Released}</li>
                    <li className="collection-item grey darken-4"><b>Runtime : </b>{this.props.movie.Runtime}</li>
                    <li className="collection-item grey darken-4"><b>Genre : </b>{this.props.movie.Genre}</li>
                    <li className="collection-item grey darken-4"><b>Director : </b>{this.props.movie.Director}</li>
                    <li className="collection-item grey darken-4"><b>Writer : </b>{this.props.movie.Writer}</li>
                    <li className="collection-item grey darken-4"><b>Actors : </b>{this.props.movie.Actors}</li>
                </ul>
            </div>
         
        )
    }
}

const mapStateToProps = state => ({
    loading: state.movies.loading,
    movie: state.movies.movie
})

export default connect(mapStateToProps, {fetchMovie})(Movie);
