import React, { Component } from 'react'
import { connect } from 'react-redux'
import {fetchMovie} from '../../actions/searchActions'

class Movie extends Component {
    componentDidMount(){
        this.props.fetchMovie(this.props.match.params.id)
    }
    render() {
        return (
            <div className="container">
                <ul className="collection with-header white-text ">
        <li className="collection-header grey darken-4"><h4>{this.props.movie.Title} - {this.props.movie.Year}</h4></li>
        <li className="collection-item grey darken-4"><b>IMDB rating : </b>{this.props.movie.Rated}</li>
                    <li className="collection-item grey darken-4"><b>Released Date : </b><p className="grey-text text-lighten-1">{this.props.movie.Released}</p></li>
                    <li className="collection-item grey darken-4"><b>Runtime : </b><p className="grey-text text-lighten-1">{this.props.movie.Runtime}</p></li>
                    <li className="collection-item grey darken-4"><b>Genre : </b><p className="grey-text text-lighten-1">{this.props.movie.Genre}</p></li>
                    <li className="collection-item grey darken-4"><b>Director : </b><p className="grey-text text-lighten-1">{this.props.movie.Director}</p></li>
                    <li className="collection-item grey darken-4"><b>Writer : </b><p className="grey-text text-lighten-1">{this.props.movie.Writer}</p></li>
                    <li className="collection-item grey darken-4"><b>Actors : </b><p className="grey-text text-lighten-1">{this.props.movie.Actors}</p></li>
                    <li className="collection-item grey darken-4"><b>Plot : </b><p className="grey-text text-lighten-1">{this.props.movie.Plot}</p></li>
                    <li className="collection-item grey darken-4"><b>Language : </b><p className="grey-text text-lighten-1">{this.props.movie.Language}</p></li>
                    <li className="collection-item grey darken-4"><b>Country : </b><p className="grey-text text-lighten-1">{this.props.movie.Country}</p></li>
                    <li className="collection-item grey darken-4"><b>Awards : </b><p className="grey-text text-lighten-1">{this.props.movie.Awards}</p></li>
                </ul>
            </div>
         
        )
    }
}

const mapStateToProps = state => ({
    loading: state.movies.loading,
    movie: state.movies.movie
})

export default connect(mapStateToProps, {fetchMovie})(Movie);
