import React, { Component } from 'react'

class About extends Component {
    render() {
        return (
            <div className="container">
            <div className="card grey darken-4 grey-text" style={{padding:'10px', margin:'auto', marginTop:'40px', marginBottom:'40px', display:'block'}}>
                <p>Get all the information you need about your favourite movies and TV shows, just by doing a simple search</p>
            </div>
            </div>
        )
    }
}
export default About
